package kim.gwon.su;

public class ArithmeticProblem {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 10;
		int num2 = 4;
		int plus = num1 + num2;
		System.out.println("num1 + num2 =" + plus);

		int subtract = num1 - num2;
		System.out.println("num1 - num2 =" + subtract);

		int multiplication = num1 * num2;
		System.out.println("num1 * num2 =" + multiplication);

		int division1 = num1 / num2;
		System.out.println("num1 / num2 =" + division1);

		double division2 = num1 / num2;
		System.out.println("num1 / num2 =" + division2);

		double division3 = (double)num1 / num2;
		System.out.println("num1 / num2 =" + division3);



	}

}
